  } finally {
    conn.release()
  }
}

const deleteBedById = async (bed_id) => {
  const conn = await pool.getConnection()
  await conn.beginTransaction()

  try {
    const [bedsdealing] = await conn.query(
      `SELECT * FROM bedsdealing WHERE bed_id = ?`,
      [bed_id]
    )

    if (bedsdealing.length > 0) {
      return {
        status: false,
        message: "",
