const selectBedsByUser = async (user_id) => {
  const conn = await pool.getConnection()
  await conn.beginTransaction()

  try {
    const [beds] = await conn.query(
      `SELECT 
      beds.id AS 'id',
      beds.amount,
      beds.address,
      beds.lat,
      beds.lng,
      beds.state,
      beds.timestamp,
      COUNT(bedsdealing.id) AS 'customer_amount'
      FROM beds
      LEFT JOIN bedsdealing ON beds.id = bedsdealing.bed_id
      WHERE beds.user_id = ?
      GROUP BY beds.id`,
      [user_id]
    )
    conn.commit()
    return {
      status: true,
      message: "",
      beds: beds,
    }
  } catch (err) {
    conn.rollback()
    return {
      status: false,
      message: err.toString(),
    }
  } finally {
    conn.release()
  }
}

