const CryptoJS = require("crypto-js");


// REGISTER
router.post("/register", async (req, res) => {
  try {
    const user = await User.findOne({ email: req.body.email });
    const idCard = await User.findOne({ idcard: req.body.idcard });

    const newUser = await new User({
      email: req.body.email,
      pass: CryptoJS.AES.encrypt(
        req.body.pass,
        process.env.PASS_SEC
      ).toString(),
      fname: req.body.fname,
      lname: req.body.lname,
      idcard: req.body.idcard,
      lineid: req.body.lineid,
      phone: req.body.phone,
    });

    if (user) {
      res
        .status(203)
        .json({ status: false, message: "" });
    } else if (idCard) {
      res
        .status(203)
        .json({ status: false, message: "" });
    } else {
      // save user and respond
      const New_User = await newUser.save();
      res.status(200).json({ status: true, message: "!" });
    }
  } catch (err) {
    res.status(500).json({
      status: false,
      message: "",
    });
  }
});

router.post("/login", async (req, res) => {
  try {
    const user = await User.findOne({ email: req.body.email });
    if (!user) {
      res
        .status(203)
        .json({
          status: false,
          message: " ",
        });
    } else {
      const hashedPass = CryptoJS.AES.decrypt(user.pass, process.env.PASS_SEC);
      const pass = hashedPass.toString(CryptoJS.enc.Utf8);

      if (pass !== req.body.pass) {
        res
          .status(203)
          .json({
            status: false,
            message: " ",
          });
      } else {

        const { pass, ...others } = user._doc;
        res
          .status(200)
          .json({
            status: true,
            message: "",
            info: { ...others },
          });
      }
    }
  } catch (err) {
    res.status(500).json({
      status: false,
      message: "",
    });
  }
});

